const dataType = require('../dataType');

module.exports = {
  validateRequired: function (varName, varValue) {
    if (!varValue && dataType.isEmpty(varValue))
      return { variable: varName, value: varValue, value: varValue, firstValue: varValue };
    return null;
  },
  validateSame: function (varName, varValue, values, dataObj) {
    if (!(values[0] in dataObj) || (varValue != dataObj[values[0]]) )
      return { variable: varName, value: varValue, value: varValue, firstValue: values[0] };
    return null;
  },
  validateRequiredIf: function (varName, varValue, values, dataObj) {
    return (
      (values[0] in dataObj) &&
      dataObj[values[0]] == values[1] &&
      dataType.isEmpty(varValue)
    ) ? { variable: varName, value: varValue, value: varValue, firstValue: varValue } :null;
  }
}