const dataType = require('../dataType');

module.exports = {
  validateRequired: function (varName, varValue) {
    if (!varValue && dataType.isEmpty(varValue))
      return { resKey: varName, resMsg: ' is required' };
    return { resKey: false, resMsg: null };
  },
  validateSame: function (varName, varValue, values, dataObj) {
    if ((values[0] in dataObj) == false)
      return { resKey: varName, resMsg: ' can not match, marks is missing from input' };
    if (varValue != dataObj[values[0]])
      return { resKey: varName, resMsg: ' is not equal to '+values[0] };
    return { resKey: false, resMsg: null };
  }
}