var a = ['','one','two','three','four', 'five','six','seven','eight','nine','ten','eleven','twelve','thirteen','fourteen','fifteen','sixteen','seventeen','eighteen','nineteen '];
var b = ['', '', 'twenty','thirty','forty','fifty', 'sixty','seventy','eighty','ninety'];

module.exports = {
  toString : function (num) {
    if (num > 99) return 'hundred';
    if (num == 0) return 'zero';
    if (num < 20) return a[num]
    return (b[Math.floor(num/10)] + a[num%10])
  },
  getUnindexKeyAndObject : function (key) {
    var i = -1
    var c = 0
    var object = {}
    while (key.indexOf("[", i) > i) {
      var val = key.substring((key.indexOf("[", i) + 1), key.indexOf("]", i))
      object[this.toString(c)] = val
      i = key.indexOf("]", i) + 1
      c ++ 
    }
    return [key.replace(/[0-9]/g, ''), object]
  },
  removeTime: function(date = new Date()) {
    return new Date(
      date.getFullYear(),
      date.getMonth(),
      date.getDate()
    );
  }
}
