const dataType = require('../dataType');

module.exports = {
  validateRequired: function (varName, varValue) {
    if (dataType.isEmpty(varValue))
      return { variable: varName, name: varName, value: varValue, first_value: varValue };
    return null;
  },
  validateSame: function (varName, varValue, values, dataObj) {
    if (!(values[0] in dataObj) || (varValue != dataObj[values[0]]) )
      return { variable: varName, name: varName, value: varValue, first_value: values[0] };
    return null;
  },
  validateRequiredIf: function (varName, varValue, values, dataObj) {
    return (
      (values[0] in dataObj) &&
      dataObj[values[0]] == values[1] &&
      dataType.isEmpty(varValue)
    ) ? { variable: varName, name: varName, value: varValue, first_value: varValue } :null;
  }
}